import { useContext } from "react";
import { RSSFeedContext } from "../../providers/RSSFeedProvider";


function useRSSFeed() {
    const {
        error,
        currentPage,
        rssFeedList,
        updateRSSFeed,
        addError,
        removeError,
        resetCurrentPage
    } = useContext(RSSFeedContext);
    return { error, currentPage, rssFeedList, updateRSSFeed, addError, removeError, resetCurrentPage };
}
export default useRSSFeed;
